export default {
  
  name: 'basicToolbar',

  getReducer: () => {
    const initialData = {
      activeTool: null
    }

    return (state = initialData, { type, payload }) => {
      switch(type){
        case 'BASIC_TOOL_ACTIVATE':
        case 'BASIC_TOOL_DEACTIVATE':
          return Object.assign({}, state, payload);
        default:
          return state;
      }
    }
  },

  doBasicToolbarActivate: ({ activeTool }) => ({ dispatch }) => {
    dispatch({
      type:'BASIC_TOOL_ACTIVATE', 
      payload: {
        activeTool: activeTool 
      }
    });
  },

  doBasicToolbarDeactivate: () => ({ dispatch }) => {
    dispatch({
      type:'BASIC_TOOL_DEACTIVATE',
      payload: {
        activeTool: null
      }
    });
  },

  selectBasicToolbarActiveTool: (state) => {
    return state.basicToolbar.activeTool;
  }
}