import { composeBundles, createCacheBundle } from "redux-bundler";
import cache from "../utils/cache";

// import out application bundles
import apiBundle from "./api-bundle";
import basicToolbarBundle from "./basic-toolbar-bundle";
import configBundle from "./config-bundle";
import exportBundle from "./export-bundle";
import identifyBundle from "./identify-bundle";
import mapBasemapBundle from "./map-basemap-bundle";
import mapCoreBundle from "./map-core-bundle";
import mapInteractionBundle from "./map-interaction-bundle";
import mapLayersBundle from "./map-layers-bundle";
import mapProjBundle from "./map-proj-bundle";
import mapViewBundle from "./map-view-bundle";
import modalWindowBundle from "./modal-window-bundle";
import notificationBundle from "./notification-bundle";
import pluginBundle from "./plugin-bundle";
import primaryPanelBundle from "./primary-panel-bundle";
import searchBundle from "./search-bundle";
import southRegionBundle from "./south-region-bundle";
import themeBundle from "./theme-bundle";
import verticalLayoutBundle from "./vertical-layout-bundle";
import viewportBundle from "./viewport-bundle";
import windowLocationBundle from "./window-location-bundle";

// compose the bundles into a redux store and export our new store
export default composeBundles(
  apiBundle,
  basicToolbarBundle,
  createCacheBundle({
    cacheFn: cache.set,
  }),
  configBundle,
  exportBundle,
  identifyBundle,
  mapBasemapBundle,
  mapCoreBundle,
  mapInteractionBundle,
  mapLayersBundle,
  mapProjBundle,
  mapViewBundle,
  modalWindowBundle,
  notificationBundle,
  pluginBundle,
  primaryPanelBundle,
  searchBundle,
  southRegionBundle,
  themeBundle,
  verticalLayoutBundle,
  viewportBundle,
  windowLocationBundle
);
