export default {

  name: 'theme',

  getReducer: () => {
    const initialData = {
      theme: 'bg-blue-chambray'
    }
    return (state = initialData, { type, payload }) => {
      switch(type){
        case 'THEME_CHANGED':
          return Object.assign({}, state, payload);
        default: 
          return state;
      }
    }
  },

  doThemeSet: (theme) => ({ dispatch }) => {
    dispatch({
      type: 'THEME_CHANGED',
      payload: {
        theme: theme
      }
    })
  },

  selectTheme: (state) => {
    return state.theme.theme;
  }
}