import React from 'react';
import { connect } from 'redux-bundler-react';
import PluginRegion from '../utils/plugin-region';

class LowerRightToolbar extends React.Component {
  render(){
    const { theme, componentsLowerRight } = this.props;
    return (
      <div className={"toolbar toolbar-lower-right " + theme} style={{ right: 49}}>
        <div className="cm-btn-group">

          <PluginRegion
            className="toolbar-horizontal-right"
            components={componentsLowerRight}
            styleOverride={{}}
          />

        </div>
      </div>
    )
  }
}

export default connect(
  'selectTheme',
  'selectComponentsLowerRight',
  LowerRightToolbar
);