import React from 'react';
import { connect } from 'redux-bundler-react'

class ModalWindow extends React.Component {
  render(){
    const { modalChildren, modalOptions } = this.props;

    if(!modalChildren || !modalChildren.length) return null;

    return (
      <div className="cm-modal show">
        <div className="cm-modal-dialog cm-modal-lg">
          <div className="cm-modal-content">
            {
              modalChildren.map((component, i) => {
                const Comp = component;
                return <Comp key={i} {...modalOptions} />
              })
            }
          </div>
        </div>
      </div>
    )
  }
}

export default connect(
  'selectModalChildren',
  'selectModalOptions',
  ModalWindow
);