import React from 'react';
import { connect } from 'redux-bundler-react';
import PluginRegion from '../utils/plugin-region';

class UpperRightToolbar extends React.Component {
  render(){
    const { theme, componentsUpperRight } = this.props;
    return (
      <div className={"toolbar toolbar-upper-right " + theme} style={{ right: 49}}>
        <div className="cm-btn-group">

          <PluginRegion
            className="toolbar-horizontal-right"
            components={ componentsUpperRight }
          />

        </div>
      </div>
    )
  }
}

export default connect(
  'selectTheme',
  'selectComponentsUpperRight',
  UpperRightToolbar
);