import React from 'react';
import SearchResultsListItem from './search-results-list-item';

class SearchResultsList extends React.Component {
  render(){
    const { items, onItemClick, selectedIdx } = this.props;
    return (
      <ul className="cm-list-group thin-scrollbar" ref={(el) => {this.el = el}} style={{position:'absolute', maxHeight: '400px', overflow: 'auto'}}>
        {
          items.map((item, i) => {
            return <SearchResultsListItem onClick={onItemClick} item={item} key={i} selected={selectedIdx === i}/>
          })
        }
      </ul>
    )
  }
}

export default SearchResultsList;