import React from 'react';
import { BasicToolbarMenuItem } from '../../application-controls';
import { connect } from 'redux-bundler-react';


class AddShapefileButton extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.handleFile = this.handleFile.bind(this);
  }

  handleClick(e){
    this.shapefileInput.click();
  }

  handleFile(e){
		const { doAddDataFile } = this.props;
    const file = this.shapefileInput.files[0];
		doAddDataFile(file);
  }

  render(){
    return (
      <BasicToolbarMenuItem
        active={ false }
        iconClass="ms ms-shp icon-inline"
        title="Add Local Shapefile"
        text="Add Local Shapefile"
        onClick={ this.handleClick }
        passProps={{}}
        disableSubmenu={true}
      >
        <input onChange={ this.handleFile } accept=".zip" className="hidden" type="file" ref={(el) => { this.shapefileInput = el}} /> 
      </BasicToolbarMenuItem>
    )
  }
}

export default connect(
  'doAddDataFile',
  AddShapefileButton
);