import React from 'react';
import { connect } from 'redux-bundler-react';
import { SidebarButton } from '../../application-controls/index';
import Catalog from './catalog-modal-window';

class CatalogButton extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    const { doModalOpen } = this.props;
    doModalOpen( {
      children: [ Catalog ],
      options: {}, 
      openedBy: 'Catalog'
    });
  }

  render() {
    return (
      <SidebarButton
        iconClass="mdi mdi-database-plus"
        label="Data Catalog"
        title="Data Catalog"
        onClick={ this.handleClick }
      />
    );
  }
}

export default connect(
  'doModalOpen',
  CatalogButton
);