import React from 'react';

class LegendItem extends React.Component {
  constructor(props){
    super(props);
    const { displayName, url, layerList } = props.layer;
    const src = url + `&service=WMS&version=1.1.1&request=GetLegendGraphic&format=image/png&sld_version=1.1.0&layer=${layerList}`;
    this.state = {
      displayName: displayName,
      src: src
    }
  }


  render(){
    const { src, displayName } = this.state;
    return (
      <li className="cm-list-group-item">
        <div className="overflow-ellipsis" title={ displayName }><strong>{ displayName }</strong></div>
        <img alt={`Example of style for ${ displayName }`} src={ src }></img>
      </li>
    )
  }
}

export default LegendItem;