const convertArea = (area, units) => {
  let output = "";
  let val = 0;
  if (units === "ft/mi") {
    // convert m² to ft²
    area = area * 10.7639;
    // if our area is larger than 1 mi² then display mi²
    // there are 5280 * 5280 = 27,878,400 ft² in 1 mi²
    if (area > 27878400) {
      // convert ft² to mi² and round to hundreths
      val = Math.round((area / 27878400) * 100) / 100;
      output = val + " mi²";
    } else {
      // round our value in ft² to hundredths
      val = Math.round(area * 100) / 100;
      output = val + " ft²";
    }
  } else if (units === "acres") {
    // since we're in imperial units convert to ft² first
    area = area * 10.7639;
    // 1 acre = 43,560 ft²
    // convert ft² to acres and round to hundredths
    val = Math.round((area / 43560) * 100) / 100;
    output = val + " acres";
  } else if (units === "hectares") {
    // 1 hectare = 10,000 m² and round to hundredths
    val = Math.round((area / 10000) * 100) / 100;
    output = val + " hectares";
  } else {
    // if we have an area bigger than 10,000 m² then show km²
    // otherwise show m²
    if (area > 10000) {
      // 1 km² === 1,000,000 m² round to hundredths
      val = Math.round((area / 1000000) * 100) / 100;
      output = val + " km²";
    } else {
      // round the original value in m² to hundredths
      val = Math.round(area * 100) / 100;
      output = val + " m²";
    }
  }
  return { output: output, val: val };
};

const convertDistance = (length, units) => {
  let output = "";
  let val = 0;
  if (units === "ft/mi") {
    // convert m to ft
    length = length * 3.28084;
    // if we're longer than a mile then display in miles
    if (length > 5280) {
      // convert ft to mi and round to hundredths
      val = Math.round((length / 5280) * 100) / 100;
      output = val + " mi";
    } else {
      // otherwise round our ft and display that
      val = Math.round(length * 100) / 100;
      output = val + " ft";
    }
  } else {
    // if we have more than 100 m then display in km
    if (length > 100) {
      // convert to km and round to hundredths
      val = Math.round((length / 1000) * 100) / 100;
      output = val + " km";
    } else {
      // otherwise display our value in m after rounding to hundredths
      val = Math.round(length * 100) / 100;
      output = val + " m";
    }
  }
  return { output: output, val: val };
};

export { convertArea, convertDistance };
