import React from "react";
import { connect } from 'redux-bundler-react';

class ThemeListItem extends React.Component {
  handleClick = () => {
    const { doThemeSet, theme } = this.props;
    doThemeSet(theme);
  };
  render() {
    const { theme } = this.props;
    const themeName = theme.replace('bg-', '');
    return (
      <li
        onClick={this.handleClick}
        title={themeName}
        className={"cm-list-group-item pointer overflow-ellipsis " + theme}
        style={{ margin: "3px", padding: "2px" }}
      >
        {themeName}
      </li>
    );
  }
}

export default connect('doThemeSet', ThemeListItem);
