import TreeviewButton from "./tree-view-button";
import bundle from "./bundle";

export const plugin = (controls) => {
  return {
    pluginName: "LayerTree",
    enabled: true,
    bundle: controls
      ? bundle(controls)
      : bundle(["toggleExpand", "addAll", "removeAll", "toggleFilter"]),
    components: [
      {
        component: TreeviewButton,
        region: "left-sidebar-top",
        sortOrder: 20,
      },
    ],
  };
};

export default plugin;
