import React from 'react';
import { connect } from 'redux-bundler-react';
import OpacitySlider from './opacity-slider';
import RemoveLayer from './remove-layer';
import LabelToggle from './label-toggle';

class IdentifyDetails extends React.Component {
  render(){
    const { treeViewSelectedItem, doRenderTreeView } = this.props;

    return (
      <div>
        <div>
          <span 
            onClick={ doRenderTreeView }
            style={{ position:'relative', top:-10, fontSize:24, cursor:'pointer' }}
          >
            <i className="mdi mdi-arrow-left"></i>
          </span>
        </div>
        <div className="thin-scrollbar sidebar-list" style={{ top: 32 }}>
          { treeViewSelectedItem.displayName }

          <OpacitySlider layer={treeViewSelectedItem.mapLayer} />

          <LabelToggle layer={treeViewSelectedItem} />

          <RemoveLayer layer={treeViewSelectedItem} />          

        </div>
      </div>
    )
  }
}

export default connect(
  'selectTreeViewSelectedItem',
  'doRenderTreeView',
  IdentifyDetails
)