import React from 'react';

class RGBOptions extends React.Component {
  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e){
    const { color, onChange } = this.props;
    onChange({ [color]: e.currentTarget.value });
  }

  render(){
    const { title, value, color } = this.props;
    return (
      <div>
        <div>{ title }</div>
        <div className="radio">
          <label className="overflow-ellipsis" title="B    459 - 479nm">
            <input onChange={ this.handleChange } checked={ value === '1'} type="radio" name={'options-' + color} value="1"></input>
            B    459 - 479nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="G    545 - 565nm">
            <input onChange={ this.handleChange } checked={ value === '2'} type="radio" name={'options-' + color} value="2"></input>
            G    545 - 565nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="R    620 - 670nm">
            <input onChange={ this.handleChange } checked={ value === '3'} type="radio" name={'options-' + color} value="3"></input>
            R    620 - 670nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="NIR  841 - 876nm">
            <input onChange={ this.handleChange } checked={ value === '4'} type="radio" name={'options-' + color} value="4"></input>
            NIR  841 - 876nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="SWIR 1230 - 1250nm">
            <input onChange={ this.handleChange } checked={ value === '5'} type="radio" name={'options-' + color} value="5"></input>
            SWIR 1230 - 1250nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="SWIR 1628 - 1652nm">
            <input onChange={ this.handleChange } checked={ value === '6'} type="radio" name={'options-' + color}  value="6"></input>
            SWIR 1628 - 1652nm
          </label>
        </div>
        <div className="radio">
          <label className="overflow-ellipsis" title="SWIR 2105 - 2155nm">
            <input onChange={ this.handleChange } checked={ value === '7'} type="radio" name={'options-' + color} value="7"></input>
            SWIR 2105 - 2155nm
          </label>
        </div>
      </div>
    )
  }
}

export default RGBOptions;