import React from "react";
import TreeList from "./tree-list";
import { connect } from "redux-bundler-react";

class TreeListContainer extends React.Component {
  render() {
    const { treeRootNode } = this.props;
    const items = treeRootNode.children || [];
    return (
      <div>
        <TreeList
          items={items}
          isFolder={true}
          collapsed={false}
          listStyle={{ padding: 0 }}
        />
      </div>
    );
  }
}

export default connect(
  "selectTreeRootNode",
  TreeListContainer
);
