import React from "react";
import { connect } from "redux-bundler-react";

class AddAll extends React.Component {
  constructor(props) {
    super(props);
    this.addAllLayers = this.addAllLayers.bind(this);
  }
  addAllLayers(e) {
    e.preventDefault();
    e.stopPropagation();
    this.props.doTreeViewAddAll();
  }

  render() {
    return (
      <button
        className="cm-btn cm-btn-default cm-btn-xs"
        style={{ marginRight: "5px" }}
        title="Add All Layers"
        onClick={this.addAllLayers}
      >
        <i className="mdi mdi-layers-triple" />
      </button>
    );
  }
}
export default connect("doTreeViewAddAll", AddAll);
