import React from "react";
import { connect } from "redux-bundler-react";

class RemoveAll extends React.Component {
  constructor(props) {
    super(props);
    this.removeAllLayers = this.removeAllLayers.bind(this);
  }
  removeAllLayers(e) {
    e.preventDefault();
    e.stopPropagation();
    this.props.doTreeViewRemoveAll();
  }

  render() {
    return (
      <button
        className="cm-btn cm-btn-default cm-btn-xs"
        style={{ marginRight: "5px" }}
        title="Turn Off All Layers"
        onClick={this.removeAllLayers}
      >
        <i className="mdi mdi-layers-triple-outline" />
      </button>
    );
  }
}
export default connect("doTreeViewRemoveAll", RemoveAll);
