import React from "react";
import { connect } from "redux-bundler-react";
import classnames from "classnames";

class ToggleExpand extends React.Component {
  constructor(props) {
    super(props);
    this.toggleExpanded = this.toggleExpanded.bind(this);
  }
  toggleExpanded(e) {
    e.preventDefault();
    e.stopPropagation();
    this.props.doTreeViewToggleExpand();
  }

  render() {
    const { treeViewToggleExpanded } = this.props;

    const toggleExpandedText = "";
    const toggleTxt = treeViewToggleExpanded
      ? "Expand All Folders"
      : "Collapse All Folders";
    const toggleExpandedIcon = classnames({
      "icon-inline": !!toggleExpandedText,
      mdi: true,
      "mdi-plus-box-outline": treeViewToggleExpanded,
      "mdi-minus-box-outline": !treeViewToggleExpanded,
    });

    return (
      <button
        className="cm-btn cm-btn-default cm-btn-xs"
        onClick={this.toggleExpanded}
        style={{ marginRight: "5px" }}
        title={toggleTxt}
      >
        <i className={toggleExpandedIcon} />
        {toggleExpandedText}
      </button>
    );
  }
}
export default connect(
  "selectTreeViewToggleExpanded",
  "doTreeViewToggleExpand",
  ToggleExpand
);
