import React from "react";
import { connect } from "redux-bundler-react";
import classnames from "classnames";

class ToggleFilter extends React.Component {
  constructor(props) {
    super(props);
    this.toggleFilter = this.toggleFilter.bind(this);
  }
  toggleFilter(e) {
    e.preventDefault();
    e.stopPropagation();
    this.props.toggleFilter();
  }

  render() {
    const { layerShowFilter } = this.props;

    const toggleFilterText = "";
    const toggleFilterIcon = classnames({
      "icon-inline": !!toggleFilterText,
      mdi: true,
      "mdi-filter-plus-outline": !layerShowFilter,
      "mdi-filter-minus-outline": layerShowFilter,
    });

    return (
      <button
        onClick={this.toggleFilter}
        className="cm-btn cm-btn-default cm-btn-xs"
        title="Show/Hide Layer Filter"
      >
        <i className={toggleFilterIcon} />
        {toggleFilterText}
      </button>
    );
  }
}
export default connect("selectLayerShowFilter", ToggleFilter);
