import React from 'react';
import { connect } from 'redux-bundler-react';
import { SidebarButton } from '../../application-controls/index';

class ZoomOutButton extends React.Component {
  constructor(props){
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    const { doZoomOut } = this.props;
    doZoomOut();
  }

  render() {
    return (
      <SidebarButton
        iconClass="mdi mdi-minus"
        label="Zoom Out"
        title="Zoom Out"
        onClick={ this.handleClick }
      />
    );
  }
}

export default connect(
  'doZoomOut',
  ZoomOutButton
);